import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { MessageSquare, Search, FileText, LogOut, Plus } from 'lucide-react';
import axios from 'axios';
import './Dashboard.css';

const Dashboard = ({ user, onLogout }) => {
  const [chats, setChats] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResult, setSearchResult] = useState('');
  const [summarizeText, setSummarizeText] = useState('');
  const [summaryResult, setSummaryResult] = useState('');
  const [activeTab, setActiveTab] = useState('chats');

  useEffect(() => {
    fetchChats();
  }, []);

  const fetchChats = async () => {
    try {
      const response = await axios.get(`/api/chat/${user.id}`);
      setChats(response.data);
    } catch (error) {
      console.error('Error fetching chats:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!searchQuery.trim()) return;

    try {
      const response = await axios.post('/api/chat/search', {
        query: searchQuery
      });
      setSearchResult(response.data.answer);
    } catch (error) {
      console.error('Search error:', error);
    }
  };

  const handleSummarize = async (e) => {
    e.preventDefault();
    if (!summarizeText.trim()) return;

    try {
      const response = await axios.post('/api/chat/summarize', {
        text: summarizeText
      });
      setSummaryResult(response.data.summary);
    } catch (error) {
      console.error('Summarize error:', error);
    }
  };

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <div className="header-content">
          <h1>Personal AI Agent</h1>
          <div className="header-actions">
            <span>Welcome, {user.username}</span>
            <button onClick={onLogout} className="btn btn-secondary">
              <LogOut size={16} />
              Logout
            </button>
          </div>
        </div>
      </header>

      <div className="dashboard-content">
        <nav className="dashboard-nav">
          <button 
            className={`nav-btn ${activeTab === 'chats' ? 'active' : ''}`}
            onClick={() => setActiveTab('chats')}
          >
            <MessageSquare size={20} />
            Chats
          </button>
          <button 
            className={`nav-btn ${activeTab === 'search' ? 'active' : ''}`}
            onClick={() => setActiveTab('search')}
          >
            <Search size={20} />
            Search
          </button>
          <button 
            className={`nav-btn ${activeTab === 'summarize' ? 'active' : ''}`}
            onClick={() => setActiveTab('summarize')}
          >
            <FileText size={20} />
            Summarize
          </button>
        </nav>

        <main className="dashboard-main">
          {activeTab === 'chats' && (
            <div className="chats-section">
              <div className="section-header">
                <h2>Your Conversations</h2>
                <Link to="/chat" className="btn btn-primary">
                  <Plus size={16} />
                  New Chat
                </Link>
              </div>
              
              {loading ? (
                <div className="loading">Loading chats...</div>
              ) : chats.length === 0 ? (
                <div className="empty-state">
                  <MessageSquare size={48} />
                  <h3>No conversations yet</h3>
                  <p>Start your first chat with the AI agent</p>
                  <Link to="/chat" className="btn btn-primary">
                    Start Chatting
                  </Link>
                </div>
              ) : (
                <div className="chats-grid">
                  {chats.map(chat => (
                    <Link 
                      key={chat._id} 
                      to={`/chat/${chat._id}`} 
                      className="chat-card"
                    >
                      <h3>{chat.title}</h3>
                      <p>{new Date(chat.updatedAt).toLocaleDateString()}</p>
                    </Link>
                  ))}
                </div>
              )}
            </div>
          )}

          {activeTab === 'search' && (
            <div className="search-section">
              <h2>Search & Ask</h2>
              <form onSubmit={handleSearch} className="search-form">
                <input
                  type="text"
                  placeholder="Ask me anything..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="input"
                />
                <button type="submit" className="btn btn-primary">
                  Search
                </button>
              </form>
              
              {searchResult && (
                <div className="result-card">
                  <h3>Answer:</h3>
                  <div className="result-content">
                    {searchResult}
                  </div>
                </div>
              )}
            </div>
          )}

          {activeTab === 'summarize' && (
            <div className="summarize-section">
              <h2>Text Summarizer</h2>
              <form onSubmit={handleSummarize} className="summarize-form">
                <textarea
                  placeholder="Paste your text here to get a summary..."
                  value={summarizeText}
                  onChange={(e) => setSummarizeText(e.target.value)}
                  className="input textarea"
                  rows="6"
                />
                <button type="submit" className="btn btn-primary">
                  Summarize
                </button>
              </form>
              
              {summaryResult && (
                <div className="result-card">
                  <h3>Summary:</h3>
                  <div className="result-content">
                    {summaryResult}
                  </div>
                </div>
              )}
            </div>
          )}
        </main>
      </div>
    </div>
  );
};

export default Dashboard;
