import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { Send, ArrowLeft, Trash2, LogOut } from 'lucide-react';
import ReactMarkdown from 'react-markdown';
import axios from 'axios';
import './Chat.css';

const Chat = ({ user, onLogout }) => {
  const { chatId } = useParams();
  const navigate = useNavigate();
  const messagesEndRef = useRef(null);

  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [chatTitle, setChatTitle] = useState('New Chat');

  useEffect(() => {
    if (chatId) {
      fetchChat();
    }
  }, [chatId, user.id]);

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const fetchChat = async () => {
    try {
      const response = await axios.get(`/api/chat/${user.id}/${chatId}`);
      setMessages(response.data.messages);
      setChatTitle(response.data.title);
    } catch (error) {
      console.error('Error fetching chat:', error);
      navigate('/dashboard');
    }
  };

  const sendMessage = async (e) => {
    e.preventDefault();
    if (!inputMessage.trim() || loading) return;

    const userMessage = inputMessage.trim();
    setInputMessage('');
    setLoading(true);

    // Add user message immediately
    const newUserMessage = {
      role: 'user',
      content: userMessage,
      timestamp: new Date()
    };
    setMessages(prev => [...prev, newUserMessage]);

    try {
      let response;
      if (chatId) {
        // Add to existing chat
        response = await axios.post(`/api/chat/${user.id}/${chatId}/message`, {
          message: userMessage
        });
        setMessages(response.data.messages);
      } else {
        // Create new chat
        response = await axios.post(`/api/chat/${user.id}`, {
          message: userMessage
        });
        setMessages(response.data.messages);
        setChatTitle(response.data.title);
        navigate(`/chat/${response.data._id}`, { replace: true });
      }
    } catch (error) {
      console.error('Error sending message:', error);
      // Remove the user message if there was an error
      setMessages(prev => prev.slice(0, -1));
    } finally {
      setLoading(false);
    }
  };

  const deleteChat = async () => {
    if (!chatId || !window.confirm('Are you sure you want to delete this chat?')) {
      return;
    }

    try {
      await axios.delete(`/api/chat/${user.id}/${chatId}`);
      navigate('/dashboard');
    } catch (error) {
      console.error('Error deleting chat:', error);
    }
  };

  return (
    <div className="chat-container">
      <header className="chat-header">
        <div className="header-left">
          <Link to="/dashboard" className="back-btn">
            <ArrowLeft size={20} />
          </Link>
          <h1>{chatId ? chatTitle : 'ðŸ’¬ AI Chat Assistant'}</h1>
        </div>
        <div className="header-right">
          <span>Welcome, {user.username}</span>
          {chatId && (
            <button onClick={deleteChat} className="btn btn-danger">
              <Trash2 size={16} />
            </button>
          )}
          <button onClick={onLogout} className="btn btn-secondary">
            <LogOut size={16} />
          </button>
        </div>
      </header>

      <div className="chat-messages">
        {messages.length === 0 ? (
          <div className="empty-chat">
            <h2>Start a conversation</h2>
            <p>Ask me anything! I can help you search, summarize, and assist with various tasks.</p>
          </div>
        ) : (
          messages.map((message, index) => (
            <div key={index} className={`message ${message.role}`}>
              <div className="message-content">
                {message.role === 'assistant' ? (
                  <ReactMarkdown>{message.content}</ReactMarkdown>
                ) : (
                  <p>{message.content}</p>
                )}
              </div>
              <div className="message-time">
                {new Date(message.timestamp).toLocaleTimeString()}
              </div>
            </div>
          ))
        )}

        {loading && (
          <div className="message assistant">
            <div className="message-content">
              <div className="typing-indicator">
                <span></span>
                <span></span>
                <span></span>
              </div>
            </div>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      <form onSubmit={sendMessage} className="chat-input-form">
        <div className="input-container">
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            placeholder="Type your message..."
            className="chat-input"
            disabled={loading}
          />
          <button
            type="submit"
            className="send-btn"
            disabled={loading || !inputMessage.trim()}
          >
            <Send size={20} />
          </button>
        </div>
      </form>
    </div>
  );
};

export default Chat;
